import React from "react";
import SideBar from "./menu.js";
import "./menu.css";
import logo from "./logo.png";
import Dropdown from "./Dropdown";
import MainBody from "./Main";
import Surahss from "./Surahs";
import Footer from "./Footer";
import { Route, BrowserRouter as Router } from "react-router-dom";
import Routie from "./Route.js";
const Head = {
  height: "70px"
};
const imgStyle = {
  padding: "15px",
  marginLeft: "60px",
  height: "65px",
  width: "156px"
};
const LangStyle = {
  position: "absolute",
  right: "20px",
  top: "10px",
  bottom: "20px",
  fontSize: "14px"
};

function Header() {
  return (
    <div id="App" className="App">
      <header id="hd" className=" header indexheader" style={Head}>
        <div>
          <SideBar /> <img src={logo} alt="logo" style={imgStyle} />
          <a style={LangStyle}>
            <Dropdown />
          </a>
        </div>
      </header>
      <MainBody />
      <Surahss />
      <Footer />
      <Router>
        <Route path="/Route" exact component={Routie} />
      </Router>
    </div>
  );
}

export default Header;
