import React from "react";
import SideBar from "./menu.js";
import "./menu.css";
import logo from "./logo.png";
import Dropdown from "./Dropdown";
import MainBody from "./Main";
import Surahss from "./Surahs";

const Head = {
  height: "70px"
};
const imgStyle = {
  padding: "15px",
  marginLeft: "35px"
};
const LangStyle = {
  position: "absolute",
  right: "20px",
  top: "10px",
  bottom: "20px",
  fontSize: "14px"
};
const SideStyle = {
  position: "absolute",
  left: "20px"
};

function Header() {
  return (
    <div id="App" className="App">
      <header id="hd" className=" header indexheader" style={Head}>
        <div>
          <SideBar style={SideStyle} />{" "}
          <img src={logo} alt="logo" style={imgStyle} />
          <a style={LangStyle}>
            <Dropdown />
          </a>
        </div>
      </header>
      <MainBody />
      <Surahss />
    </div>
  );
}

export default Header;
